import { Injectable } from '@angular/core';
import { Item } from '../model/item';

@Injectable({
  providedIn: 'root'
})
export class PrintPDFService {

  pdfMake: any;

  constructor() { }

  async loadPdfMaker() {
    if (!this.pdfMake) {
      const pdfMakeModule = await import('pdfmake/build/pdfmake');
      const pdfFontsModule = await import('pdfmake/build/vfs_fonts');
      this.pdfMake = pdfMakeModule.default;
      this.pdfMake.vfs = pdfFontsModule.default.pdfMake.vfs;
    }
  }

  async generatePdf(totalValue,itemArray) {

    await this.loadPdfMaker();

    const def = this.getDocumentDefinition(totalValue,itemArray);
    this.pdfMake.createPdf(def).open();
  }

  formatCurrentDate() {
    var dateString= new Date().toLocaleString('en-AU');
    var currentDate = new Date(dateString);
    var date = currentDate.toLocaleDateString('en-US');
    var hours = currentDate.getHours();
    var minutes = currentDate.getMinutes();
    var ampm = hours >= 12 ? 'PM' : 'AM';
    hours = hours % 12;
    hours = hours ? hours : 12; 
    var mins = minutes < 10 ? '0'+minutes : minutes;
    var finalDate = date+' '+hours + ':' + mins + ' ' + ampm;
    return finalDate;
  }

  getDocumentDefinition(totalValue,itemArray){
    console.log(totalValue);
    var dataArray =[];
    itemArray.forEach(element => {
      var dataObj = [];
      dataObj.push(element.name);
      dataObj.push("$"+element.rate);
      dataObj.push(element.quantity);
      dataObj.push("$"+element.amount);
      dataArray.push(dataObj);
    });
 
    this.pdfMake.tableLayouts = {
      myLayout: {
        hLineWidth: function (i, node) {
          if (i === 0 || i === node.table.body.length) {
            return 1;
          }
          return 0;
        },
        vLineWidth: function (i, node) {
          if (i === 0 || i === node.table.body.length+1) {
            return 1;
          }
          return 1;
        },
        hLineColor: function (i) {
          return i === 1 ? 'black' : '#aaa';
        },
        paddingLeft: function (i) {
          return 8;
        },
        paddingTop: function (i, node) {
          return 8;
        },
        paddingBottom: function (i, node) {
          return 8;
        }
      }
    };



    var docDefinition = {
      pageSize: 'A4',
      pageMargins: [ 40, 140, 40, 60 ],
      // header: [{text: 'Report ',fontSize:30, alignment: 'left',margin:[40,10,0,0], width:'40%'},{text: this.formatCurrentDate(),fontSize:12, alignment: 'left', margin:[0,0,0,20] }],
      header:[{
        columns: [
          {
            // auto-sized columns have their widths based on their content
            width: '*',
            text: 'Report',
            fontSize:30,
            margin:[40,20,0,0]
          },
          {
            // star-sized columns fill the remaining space
            // if there's more than one star-column, available width is divided equally
            width: 80,
            text: ''
          },
          {
            // fixed width
            width: '*',
            text: this.formatCurrentDate(),
            fontSize:14,
            margin:[40,35,0,0]
          },
          {
            // % width
            image: 'data:image/png;base64,',
            width:160,
            height:100,
            margin:[0,0,60,0]
          }
        ],
        // optional space between columns
        columnGap: 0
      },{ canvas: [ { type: 'rect', x: 40, y: -40, w: 380, h: 1 } ] }],
      footer:[{ canvas: [ { type: 'rect', x: 40, y: 0, w: 520, h: 1 } ] },{
        columns: [
          {
            // auto-sized columns have their widths based on their content
            width: 'auto',
            text: 'Ace Contractors Group Pty. Ltd.',
            fontSize:16,
            margin:[40,10,0,0]
          },
          {
            // fixed width
            width: '*',
            text: 'ABN 12 345 678 901',
            fontSize:16,
            margin:[0,10,20,0]
          }
        ],
        // optional space between columns
        columnGap: 150
      }],
      content: [
        
        {
          layout: 'noBorders', // optional
          table: {
            // headers are automatically repeated if the table spans over multiple pages
            // you can declare how many rows should be treated as headers
            headerRows: 1,
            widths: [ '*', 100, 100, '*' ],
    
            body: [
              [ { text: 'Item', bold: true, alignment: 'center' }, { text: 'Rate', bold: true, alignment: 'center' }, { text: 'Quantity', bold: true, alignment: 'center' }, { text: 'Amount', bold: true, alignment: 'center' } ]
            ]
          },
          style: 'itemHeader'
        },
        {
          layout: 'myLayout', // optional
          table: {
            // headers are automatically repeated if the table spans over multiple pages
            // you can declare how many rows should be treated as headers
            headerRows: 1,
            widths: [ '*', 100, 100, '*' ],
    
            body: dataArray
          }
        }, {
          layout: 'noBorders', // optional
          table: {
            // headers are automatically repeated if the table spans over multiple pages
            // you can declare how many rows should be treated as headers
            headerRows: 1,
            widths: [ '*', 100, 100, '*' ],
    
            body: [
              [ '', '', { text: 'Total: ', bold: true, fontSize:12,alignment: 'right' ,margin:[0,10,0,0]}, { text: "$ "+totalValue , bold: true, fontSize:12,alignment: 'left' ,margin:[0,10,0,0]} ]
            ]
          }
        },{
          text:'Ace Person',
          margin:[0,100,0,0]
        },{
          text:'Project Manager',
          margin:[0,5,0,0],
          bold: true,
          startPosition: {
            left: 60
          }
        }
      ],
      styles:{
        itemHeader:{
          margin: [0,0,0,0]
        }
      }
    };
    return docDefinition;
  }

}
